mapper.select:
SELECT pay_type, pay_res_id, pay_mem_id, pay_date, pay_cost
FROM TBL_PAYMENT
WHERE pay_mem_id = #{pay_mem_id}

select mem_point
from TBL_MEMBER
where mem_id = #{mem_id};

SELECT res_rid
FROM TBL_RESERVE
WHERE res_rid = #{pay_res_rid}

SELECT non_rid
FROM TBL_NONMEMBER
WHERE non_rid = #{pay_res_rid}
