mapper.select:
SELECT pay_res_rid, pay_type, pay_mem_id, pay_date
FROM TBL_PAYMENT
WHERE pay_mem_id = #{pay_mem_id}

select mem_point
from TBL_MEMBER
where mem_id = #{pay_mem_id};

SELECT non_rid
FROM TBL_NONMEMBER
WHERE non_rid = #{pay_res_rid}

mapper.update:
    	update tbl_reserve set
		res_status = '결제완료'
		where res_rid = #{res_rid}
