mapper.select:
select * from rent001.TBL_MEMBER
where mem_id = #{mem_id}
and mem_pw = #{mem_pw}

select * from rent001.TBL_NONMEMBER
where non_name = #{non_name}
and non_tel = #{non_tel}

select mem_id from rent001.TBL_MEMBER
where mem_name =#{mem_name} 
and mem_phone = #{mem_phone}

mapper.update:
update rent001.TBL_MEMBER set
	mem_pw = #{mem_pw}
where mem_id = #{mem_id}

mapper.select:
select *
from rent001.TBL_MEMBER
where mem_pw = #{mem_pw}
and mem_id = #{mem_id}
